import React, { Component } from 'react';

export class App extends Component {
    state={ 
        amount: '',
        requiredNotes: {2000: 0,500: 0,200: 0,100: 0,50: 0,20: 0,10: 0,5: 0,2: 0,1: 0},
        notes: [2000,500,200,100,50,20,10,5,2,1],
    }
    handleChange = ( e ) => {
        this.setState({
            [e.target.name] : e.target.value 
        },()=> { this.calNotes() });
    }
    calNotes = () => {
        let amount = this.state.amount;
        const notes = this.state.notes;
        let requiredNotes = this.state.requiredNotes;
        notes.map( ( el ) => {
            let c = Math.floor(amount / el);
            amount = amount - el * Math.floor(amount / el);
            requiredNotes = {...requiredNotes,[el]: c }
            return true;
        });
        this.setState({ requiredNotes })

    }
    renderNotes = () => {
        return Object.entries(this.state.requiredNotes).reverse().map((el ,i ) => {
            return (
                    <div role="listitem" key={ i } className="item">
                        <strong> { el[0] } </strong> rs. notes will be <strong> { el[1] !== 0 ? el[1] : 0} </strong>
                    </div>
            );
        })
    }
    totalNotes = () => {
        return this.state.requiredNotes && Object.entries(this.state.requiredNotes).reduce( ( a,num ) => {
             return  a + num[1];
        }, 0);
    }
    handleSubmit = ( e ) => {
        e.preventDefault();
    }
    render() {
        return (
            <div className='ui container'>
                <div className='custom-div'>
                    <h1 className='ui header'> 
                        welcome to atm 
                    </h1>
                </div>

                <div className="ui segment">
                    <div className="ui two column very relaxed grid">
                    <div className="column">
                        <form className="ui form" onSubmit={ this.handleSubmit }>
                            <div className="field">
                            <label>Enter Amount</label>
                            <input type="text" name="amount" onChange={ this.handleChange } value={ this.state.amount } placeholder="Enter Amount" />
                            </div>
                            <button className="ui button" type="submit">Submit</button>
                        </form>
                    </div>
                    <div className="column">
                        <div role="list" className="ui list">
                            { this.renderNotes() }
                        </div>
                        <div>
                            <h2>
                                Total numbers of Notes : <strong>{ this.totalNotes() }</strong>
                            </h2>
                        </div>
                    </div>
                </div>
                <div className="ui vertical divider">
                and
                </div>
                </div>
            </div>
        )
    }
}
