import React, { Component } from 'react';

export class App extends Component {
    state={ 
        amount: '',
        requiredNotes: [],
        notes: [2000,500,200,100,50,20,10,5,2,1],
    }
    handleChange = ( e ) => {
        this.setState({
            [e.target.name] : e.target.value 
        },()=> { this.calNotes() });
    }
    calNotes = () => {
        let amount = this.state.amount;
        const notes = this.state.notes;
        const count = {};
        // console.log(notes, amount);
        // notes.map((el) => {
        //     let rem = amount % el;
        //     console.log(rem, '- note : ', el, ' - ', Math.floor(amount / el) );
        //     return true;
        // })

        if (amount > 0) {
            let c = 0;
            return notes.map(each => {
                if (amount > each && notes[each] > 0) {
                    c = parseInt(amount / each);
                    if (c <= notes[each]) {
                        count[each] = c;
                        amount = amount % each;
                    } else{
                        count[each] = notes[each];
                        amount = amount - (each * notes[each]);
                    }
                }
            }
            );
        }
    }
    handleSubmit = ( e ) => {
        e.preventDefault();
    }
    render() {
        return (
            <div className='ui container'>
                <div className='custom-div'>
                    <h1 className='ui header'> 
                        welcome to atm 
                    </h1>
                </div>

                <div className="ui segment">
                    <div className="ui two column very relaxed grid">
                    <div className="column">
                        <form className="ui form" onSubmit={ this.handleSubmit }>
                            <div className="field">
                            <label>Enter Amount</label>
                            <input type="text" name="amount" onChange={ this.handleChange } value={ this.state.amount } placeholder="Enter Amount" />
                            </div>
                            <button className="ui button" type="submit">Submit</button>
                        </form>
                    </div>
                    <div className="column">
                        <p></p>
                        <p></p>
                        <p></p>
                        <p></p>
                    </div>
                </div>
                <div className="ui vertical divider">
                and
                </div>
                </div>
            </div>
        )
    }
}
