import React, { Component } from 'react';

export class App extends Component {
    state={ 
        amount: '',
        requiredNotes: {},
        notes: [2000,500,200,100,50,20,10,5,2,1],
    }
    handleChange = ( e ) => {
        this.setState({
            [e.target.name] : e.target.value 
        },()=> { this.calNotes() });
    }
    calNotes = () => {
        let amount = this.state.amount;
        const notes = this.state.notes;
        let requiredNotes = this.state.requiredNotes;
        console.log(notes, amount);
        notes.map((el) => {
            let rem = amount % el;
            let c = Math.floor(amount / el);
            console.log('want add : ',rem, '- note : ', el, ' - qty ', Math.floor(amount / el) );
            amount = amount - el * Math.floor(amount / el);
            requiredNotes = {...requiredNotes,[el]: c }
            return true;
        });
        console.log('TEST 2 : ',requiredNotes);
        this.setState({ requiredNotes })


    }
    renderNotes = () => {
        // console.log('TEST : ',this.state.requiredNotes);
        return this.state.requiredNotes && Object.entries(this.state.requiredNotes).map((el ,i ) => {
            return (
                    <div role="listitem" key={ i } className="item"><strong> { el[0] } </strong> rs. notes will be <strong> { el[1]} </strong></div>
            );
        })
    }
    handleSubmit = ( e ) => {
        e.preventDefault();
    }
    render() {
        return (
            <div className='ui container'>
                <div className='custom-div'>
                    <h1 className='ui header'> 
                        welcome to atm 
                    </h1>
                </div>

                <div className="ui segment">
                    <div className="ui two column very relaxed grid">
                    <div className="column">
                        <form className="ui form" onSubmit={ this.handleSubmit }>
                            <div className="field">
                            <label>Enter Amount</label>
                            <input type="text" name="amount" onChange={ this.handleChange } value={ this.state.amount } placeholder="Enter Amount" />
                            </div>
                            <button className="ui button" type="submit">Submit</button>
                        </form>
                    </div>
                    <div className="column">
                        <div role="list" className="ui list">
                            { this.renderNotes() }
                        </div>
                    </div>
                </div>
                <div className="ui vertical divider">
                and
                </div>
                </div>
            </div>
        )
    }
}
